import React, { useEffect, useState } from 'react';
import { fetchOdds } from '../api';

const App = () => {
  interface Game {
    home_team: string;
    away_team: string;
    bookmakers: Array<{
      key: string;
      title: string;
      markets: Array<{
        key: string;
        outcomes: Array<{
          name: string;
          price: number;
        }>;
      }>;
    }>;
  }

  const [odds, setOdds] = useState<Game[]>([]);

  useEffect(() => {
    const getOdds = async () => {
      try {
        const data = await fetchOdds();
        setOdds(data);
      } catch (error) {
        console.error('Failed to fetch odds:', error);
      }
    };

    getOdds();
  }, []);

  // Function to calculate implied probability
  const calculateImpliedProbability = (odds: number) => {
    return 1 / odds;
  };

  // Function to check for arbitrage opportunities for a specific market
  const checkArbitrage = (homeOdds: number, awayOdds: number) => {
    const homeProb = calculateImpliedProbability(homeOdds);
    const awayProb = calculateImpliedProbability(awayOdds);
    // Only show arbitrage if the total probability is less than 0.8
    return homeProb + awayProb < 0.8;
  };

  // Function to find outcomes for a given market (H2H, spreads, totals)
  const findOutcomes = (markets: Array<any>, marketType: string, teamName: string) => {
    const market = markets.find(m => m.key === marketType);
    if (market) {
      return market.outcomes.find(o => o.name === teamName);
    }
    return null;
  };

  return (
    <div>
      <h1>NBA Arbitrage Odds</h1>
      <ul>
        {odds.map((game, index) => (
          <li key={index}>
            <h3>
              {game.home_team} vs {game.away_team}
            </h3>
            <ul>
              {game.bookmakers.map((homeBookmaker) => (
                game.bookmakers.map((awayBookmaker) => {
                  // Only check arbitrage if bookmakers are different
                  if (homeBookmaker.key !== awayBookmaker.key) {
                    // Check for arbitrage in H2H market
                    const homeH2H = findOutcomes(homeBookmaker.markets, 'h2h', game.home_team);
                    const awayH2H = findOutcomes(awayBookmaker.markets, 'h2h', game.away_team);
                    const homeSpread = findOutcomes(homeBookmaker.markets, 'spreads', game.home_team);
                    const awaySpread = findOutcomes(awayBookmaker.markets, 'spreads', game.away_team);
                    const homeTotal = findOutcomes(homeBookmaker.markets, 'totals', game.home_team);
                    const awayTotal = findOutcomes(awayBookmaker.markets, 'totals', game.away_team);

                    // Check for arbitrage in H2H market
                    if (homeH2H && awayH2H) {
                      const homeOddsH2H = homeH2H.price;
                      const awayOddsH2H = awayH2H.price;
                      if (checkArbitrage(homeOddsH2H, awayOddsH2H)) {
                        return (
                          <li key={`${homeBookmaker.key}-${awayBookmaker.key}-H2H`}>
                            <strong>{homeBookmaker.title} (Home) vs {awayBookmaker.title} (Away) - H2H</strong>
                            <ul>
                              <li>{game.home_team}: {homeOddsH2H}</li>
                              <li>{game.away_team}: {awayOddsH2H}</li>
                              <li><strong>Arbitrage Opportunity!</strong></li>
                            </ul>
                          </li>
                        );
                      }
                    }

                    // Check for arbitrage in Spreads market
                    if (homeSpread && awaySpread) {
                      const homeOddsSpread = homeSpread.price;
                      const awayOddsSpread = awaySpread.price;
                      if (checkArbitrage(homeOddsSpread, awayOddsSpread)) {
                        return (
                          <li key={`${homeBookmaker.key}-${awayBookmaker.key}-Spreads`}>
                            <strong>{homeBookmaker.title} (Home) vs {awayBookmaker.title} (Away) - Spreads</strong>
                            <ul>
                              <li>{game.home_team}: {homeOddsSpread}</li>
                              <li>{game.away_team}: {awayOddsSpread}</li>
                              <li><strong>Arbitrage Opportunity!</strong></li>
                            </ul>
                          </li>
                        );
                      }
                    }

                    // Check for arbitrage in Totals market
                    if (homeTotal && awayTotal) {
                      const homeOddsTotal = homeTotal.price;
                      const awayOddsTotal = awayTotal.price;
                      if (checkArbitrage(homeOddsTotal, awayOddsTotal)) {
                        return (
                          <li key={`${homeBookmaker.key}-${awayBookmaker.key}-Totals`}>
                            <strong>{homeBookmaker.title} (Home) vs {awayBookmaker.title} (Away) - Totals</strong>
                            <ul>
                              <li>{game.home_team}: {homeOddsTotal}</li>
                              <li>{game.away_team}: {awayOddsTotal}</li>
                              <li><strong>Arbitrage Opportunity!</strong></li>
                            </ul>
                          </li>
                        );
                      }
                    }
                  }
                  return null;
                })
              ))}
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
