import Home from '../pages/Home';
import PlayerPropsAssists from '../pages/playerPropsAssists';
import H2hSpreadTotal from '../pages/h2hSpreadTotal';
import PlayerPropsRebounds from '../pages/playerPropsRebounds';
import PlayerPropsPoints from '../pages/playerPropsPoints';
import { Box, Flex, Link, Spacer, useBreakpointValue, Button } from '@chakra-ui/react';
import { Routes, Route, Link as RouterLink } from 'react-router-dom';

const App = () => {
  // Change the color scheme based on the current mode
  const linkColor = useBreakpointValue({ base: 'teal.500', md: 'blue.500' });

  return (
    <Box p={4}>
      {/* Navigation Bar */}
      <Flex as="nav" mb={6} align="center" justify="space-between" bg="teal.800" p={4} borderRadius="md" boxShadow="md">
        <Flex>
          <Link as={RouterLink} to="/" p={3} fontSize="lg" fontWeight="bold" color="white" _hover={{ color: 'teal.300' }}>
            Home
          </Link>
          <Link as={RouterLink} to="/h2h-spread-total" p={3} fontSize="lg" color="white" _hover={{ color: 'teal.300' }}>
            H2H - Spreads - Totals
          </Link>
          <Link as={RouterLink} to="/player-props-assists" p={3} fontSize="lg" color="white" _hover={{ color: 'teal.300' }}>
            Player Props - Assists
          </Link>
          <Link as={RouterLink} to="/player-props-rebounds" p={3} fontSize="lg" color="white" _hover={{ color: 'teal.300' }}>
            Player Props - Rebounds
          </Link>
          <Link as={RouterLink} to="/player-props-points" p={3} fontSize="lg" color="white" _hover={{ color: 'teal.300' }}>
            Player Props - Points
          </Link>
        </Flex>
        <Spacer />
        {/* Optional button for additional functionality */}
        <Button colorScheme="teal" variant="outline" size="sm" borderRadius="md">
          Sign In
        </Button>
      </Flex>

      {/* Routes */}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/h2h-spread-total" element={<H2hSpreadTotal />} />
        <Route path="/player-props-assists" element={<PlayerPropsAssists />} />
        <Route path="/player-props-rebounds" element={<PlayerPropsRebounds />} />
        <Route path="/player-props-points" element={<PlayerPropsPoints />} />
      </Routes>
    </Box>
  );
};

export default App;
