import React, { useEffect, useState } from 'react';
import { fetchOdds } from '../api';

const App = () => {
  interface Game {
    home_team: string;
    away_team: string;
    bookmakers: Array<{
      key: string;
      title: string;
      markets: Array<{
        key: string;
        outcomes: Array<{
          name: string;
          price: number;
        }>;
      }>;
    }>;
  }

  const [odds, setOdds] = useState<Game[]>([]);

  useEffect(() => {
    const getOdds = async () => {
      try {
        const data = await fetchOdds();
        setOdds(data);
      } catch (error) {
        console.error('Failed to fetch odds:', error);
      }
    };

    getOdds();
  }, []);

  return (
    <div>
      <h1>NBA Odds</h1>
      <ul>
        {odds.map((game, index) => (
          <li key={index}>
            <h3>
              {game.home_team} vs {game.away_team}
            </h3>
            <ul>
              {game.bookmakers.map((bookmaker) => (
                <li key={bookmaker.key}>
                  <strong>{bookmaker.title}</strong>:
                  <ul>
                    {bookmaker.markets.map((market) => (
                      <li key={market.key}>
                        <ul>
                          {market.outcomes.map((outcome) => (
                            <li key={outcome.name}>
                              {outcome.name}: {outcome.price}
                            </li>
                          ))}
                        </ul>
                      </li>
                    ))}
                  </ul>
                </li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
