import React, { JSX, useEffect, useState } from 'react';
import { fetchH2HOdds, fetchSpreadOdds, fetchTotalOdds } from '../api';

const App = () => {
  interface Game {
    home_team: string;
    away_team: string;
    bookmakers: Array<{
      key: string;
      title: string;
      markets: Array<{
        key: string;
        outcomes: Array<{
          name: string;
          price: number;
        }>;
      }>;
    }>;
  }

  const [h2hOdds, setH2HOdds] = useState<Game[]>([]);
  const [spreadOdds, setSpreadOdds] = useState<Game[]>([]);
  const [totalOdds, setTotalOdds] = useState<Game[]>([]);

  useEffect(() => {
    const getOdds = async () => {
      try {
        const h2hData = await fetchH2HOdds();
        const spreadData = await fetchSpreadOdds();
        const totalData = await fetchTotalOdds();

        setH2HOdds(h2hData);
        setSpreadOdds(spreadData);
        setTotalOdds(totalData);
      } catch (error) {
        console.error('Failed to fetch odds:', error);
      }
    };

    getOdds();
  }, []);

  const calculateImpliedProbability = (odds: number) => {
    return 1 / odds;
  };

  const checkArbitrage = (homeOdds: number, awayOdds: number) => {
    const homeProb = calculateImpliedProbability(homeOdds);
    const awayProb = calculateImpliedProbability(awayOdds);
    return homeProb + awayProb < 1;
  };

  const findOutcomes = (
    markets: Array<{ key: string; outcomes: Array<{ name: string; price: number }> }>,
    marketType: string,
    teamName: string
  ) => {
    const market = markets.find(m => m.key === marketType);
    if (market) {
      return market.outcomes.find((o: { name: string; price: number }) => o.name === teamName);
    }
    return null;
  };

  const arbitrageH2HBets: JSX.Element[] = [];
  const arbitrageSpreadsBets: JSX.Element[] = [];
  const arbitrageTotalsBets: JSX.Element[] = [];

  const oddsH2H: JSX.Element[] = [];
  const oddsSpreads: JSX.Element[] = [];
  const oddsTotals: JSX.Element[] = [];

  const processBets = (
    game: Game,
    marketType: string,
    arbitrageList: JSX.Element[],
    oddsList: JSX.Element[]
  ) => {
    game.bookmakers.forEach((homeBookmaker) => {
      game.bookmakers.forEach((awayBookmaker) => {
        if (homeBookmaker.key !== awayBookmaker.key) {
          const homeMarket = findOutcomes(homeBookmaker.markets, marketType, game.home_team);
          const awayMarket = findOutcomes(awayBookmaker.markets, marketType, game.away_team);

          if (homeMarket && awayMarket) {
            const homeOdds = homeMarket.price;
            const awayOdds = awayMarket.price;

            if (checkArbitrage(homeOdds, awayOdds)) {
              arbitrageList.push(
                <li key={`${homeBookmaker.key}-${awayBookmaker.key}-${marketType}-arb`}>
                  <strong>{homeBookmaker.title} (Home) vs {awayBookmaker.title} (Away) - {marketType}</strong>
                  <ul>
                    <li>{game.home_team}: {homeOdds}</li>
                    <li>{game.away_team}: {awayOdds}</li>
                    <li><strong>Arbitrage Opportunity!</strong></li>
                  </ul>
                </li>
              );
            }
          }

          if (homeMarket || awayMarket) {
            oddsList.push(
              <li key={`${homeBookmaker.key}-${awayBookmaker.key}-${marketType}-odds`}>
                <strong>{homeBookmaker.title} (Home) vs {awayBookmaker.title} (Away) - {marketType}</strong>
                <ul>
                  {homeMarket ? <li>{game.home_team}: {homeMarket.price}</li> : null}
                  {awayMarket ? <li>{game.away_team}: {awayMarket.price}</li> : null}
                </ul>
              </li>
            );
          }
        }
      });
    });
  };

  h2hOdds.forEach((game) => processBets(game, 'h2h', arbitrageH2HBets, oddsH2H));
  spreadOdds.forEach((game) => processBets(game, 'spreads', arbitrageSpreadsBets, oddsSpreads));
  totalOdds.forEach((game) => processBets(game, 'totals', arbitrageTotalsBets, oddsTotals));

  return (
    <div style={{ padding: '20px' }}>
      <h1>NBA Arbitrage Odds</h1>

      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
        {/* Arbitrage H2H */}
        <div style={{ flex: 1, minWidth: '300px' }}>
          <h2>Arbitrage H2H</h2>
          <ul>
            {arbitrageH2HBets.length > 0 ? arbitrageH2HBets : <li>No arbitrage opportunities found.</li>}
          </ul>
        </div>

        {/* Arbitrage Spreads */}
        <div style={{ flex: 1, minWidth: '300px' }}>
          <h2>Arbitrage Spreads</h2>
          <ul>
            {arbitrageSpreadsBets.length > 0 ? arbitrageSpreadsBets : <li>No arbitrage opportunities found.</li>}
          </ul>
        </div>

        {/* Arbitrage Totals */}
        <div style={{ flex: 1, minWidth: '300px' }}>
          <h2>Arbitrage Totals</h2>
          <ul>
            {arbitrageTotalsBets.length > 0 ? arbitrageTotalsBets : <li>No arbitrage opportunities found.</li>}
          </ul>
        </div>
      </div>

      <hr style={{ margin: '40px 0' }} />

      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
        {/* Odds H2H */}
        <div style={{ flex: 1, minWidth: '300px' }}>
          <h2>Odds H2H</h2>
          <ul>
            {oddsH2H.length > 0 ? oddsH2H : <li>No H2H odds found.</li>}
          </ul>
        </div>

        {/* Odds Spreads */}
        <div style={{ flex: 1, minWidth: '300px' }}>
          <h2>Odds Spreads</h2>
          <ul>
            {oddsSpreads.length > 0 ? oddsSpreads : <li>No spreads odds found.</li>}
          </ul>
        </div>

        {/* Odds Totals */}
        <div style={{ flex: 1, minWidth: '300px' }}>
          <h2>Odds Totals</h2>
          <ul>
            {oddsTotals.length > 0 ? oddsTotals : <li>No totals odds found.</li>}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default App;
