import React, { useEffect, useState } from 'react';
import { fetchH2HOdds, fetchSpreadOdds, fetchTotalOdds } from '../api';

const App = () => {
  interface Outcome {
    name: string;
    price: number;
    point?: number;
  }

  interface Market {
    key: string;
    outcomes: Outcome[];
  }

  interface Bookmaker {
    key: string;
    title: string;
    markets: Market[];
  }

  interface Game {
    home_team: string;
    away_team: string;
    bookmakers: Bookmaker[];
  }

  const [h2hOdds, setH2HOdds] = useState<Game[]>([]);
  const [spreadOdds, setSpreadOdds] = useState<Game[]>([]);
  const [totalOdds, setTotalOdds] = useState<Game[]>([]);

  useEffect(() => {
    const getOdds = async () => {
      try {
        setH2HOdds(await fetchH2HOdds());
        setSpreadOdds(await fetchSpreadOdds());
        setTotalOdds(await fetchTotalOdds());
      } catch (error) {
        console.error('Failed to fetch odds:', error);
      }
    };

    getOdds();
  }, []);

  const renderBookmakerOdds = (bookmaker: Bookmaker, marketType: string, isSpreadOrTotal = false) => {
    const market = bookmaker.markets.find(m => m.key === marketType);
    if (!market) return null;

    return (
      <li key={bookmaker.key}>
        <strong>{bookmaker.title}</strong>
        <ul>
          {market.outcomes.map(o => (
            <li key={o.name}>
              {o.name}: {o.price}{isSpreadOrTotal && o.point !== undefined ? ` (Line: ${o.point})` : ''}
            </li>
          ))}
        </ul>
      </li>
    );
  };

  const renderMatchOdds = (games: Game[], marketType: string, isSpreadOrTotal = false) => {
    return games.map(game => (
      <li key={`${game.home_team}-${game.away_team}-${marketType}`}
          style={{ marginBottom: '1rem' }}>
        <strong>{game.home_team} vs {game.away_team}</strong>
        <ul>
          {game.bookmakers.map(bm => renderBookmakerOdds(bm, marketType, isSpreadOrTotal))}
        </ul>
      </li>
    ));
  };

  return (
    <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '2rem' }}>
      <div>
        <h2>H2H Odds</h2>
        <ul>{h2hOdds.length > 0 ? renderMatchOdds(h2hOdds, 'h2h') : <li>No H2H odds found.</li>}</ul>
      </div>

      <div>
        <h2>Spread Odds</h2>
        <ul>{spreadOdds.length > 0 ? renderMatchOdds(spreadOdds, 'spreads', true) : <li>No Spread odds found.</li>}</ul>
      </div>

      <div>
        <h2>Total Odds</h2>
        <ul>{totalOdds.length > 0 ? renderMatchOdds(totalOdds, 'totals', true) : <li>No Total odds found.</li>}</ul>
      </div>
    </div>
  );
};

export default App;
