import React, { JSX, useEffect, useState } from 'react';
import { fetchH2HOdds, fetchSpreadOdds, fetchTotalOdds } from '../api';

const App = () => {
  interface Game {
    home_team: string;
    away_team: string;
    bookmakers: Array<{
      key: string;
      title: string;
      markets: Array<{
        key: string;
        outcomes: Array<{
          name: string;
          price: number;
        }> ;
      }> ;
    }> ;
  }

  const [h2hOdds, setH2HOdds] = useState<Game[]>([]);
  const [spreadOdds, setSpreadOdds] = useState<Game[]>([]);
  const [totalOdds, setTotalOdds] = useState<Game[]>([]);

  useEffect(() => {
    const getOdds = async () => {
      try {
        const h2hData = await fetchH2HOdds();
        const spreadData = await fetchSpreadOdds();
        const totalData = await fetchTotalOdds();

        setH2HOdds(h2hData);
        setSpreadOdds(spreadData);
        setTotalOdds(totalData);
      } catch (error) {
        console.error('Failed to fetch odds:', error);
      }
    };

    getOdds();
  }, []);

  // Function to calculate implied probability
  const calculateImpliedProbability = (odds: number) => {
    return 1 / odds;
  };

  // Function to check for arbitrage opportunities
  const checkArbitrage = (homeOdds: number, awayOdds: number) => {
    const homeProb = calculateImpliedProbability(homeOdds);
    const awayProb = calculateImpliedProbability(awayOdds);
    return homeProb + awayProb < 1;  // Arbitrage if the total probability is less than 1
  };

  // Function to find outcomes for a given market (H2H, spreads, totals)
  const findOutcomes = (
    markets: Array<{ key: string; outcomes: Array<{ name: string; price: number }> }>,
    marketType: string,
    teamName: string
  ) => {
    const market = markets.find(m => m.key === marketType);
    if (market) {
      return market.outcomes.find((o: { name: string; price: number }) => o.name === teamName);
    }
    return null;
  };

  const arbitrageBets: JSX.Element[] = [];
  const totalsBets: JSX.Element[] = [];

  // Helper function to push bets to arbitrageBets and totalsBets
  const processBets = (game: Game, marketType: string, arbitrageBets: JSX.Element[], totalsBets: JSX.Element[]) => {
    game.bookmakers.forEach((homeBookmaker) => {
      game.bookmakers.forEach((awayBookmaker) => {
        if (homeBookmaker.key !== awayBookmaker.key) {
          const homeMarket = findOutcomes(homeBookmaker.markets, marketType, game.home_team);
          const awayMarket = findOutcomes(awayBookmaker.markets, marketType, game.away_team);

          if (homeMarket && awayMarket) {
            const homeOdds = homeMarket.price;
            const awayOdds = awayMarket.price;

            if (checkArbitrage(homeOdds, awayOdds)) {
              arbitrageBets.push(
                <li key={`${homeBookmaker.key}-${awayBookmaker.key}-${marketType}`}>
                  <strong>{homeBookmaker.title} (Home) vs {awayBookmaker.title} (Away) - {marketType}</strong>
                  <ul>
                    <li>{game.home_team}: {homeOdds}</li>
                    <li>{game.away_team}: {awayOdds}</li>
                    <li><strong>Arbitrage Opportunity!</strong></li>
                  </ul>
                </li>
              );
            }
          }

          // Add the total odds to the totalsBets list, regardless of arbitrage
          if (homeMarket || awayMarket) {
            totalsBets.push(
              <li key={`${homeBookmaker.key}-${awayBookmaker.key}-${marketType}`}>
                <strong>{homeBookmaker.title} (Home) vs {awayBookmaker.title} (Away) - {marketType}</strong>
                <ul>
                  {homeMarket ? <li>{game.home_team}: {homeMarket.price}</li> : null}
                  {awayMarket ? <li>{game.away_team}: {awayMarket.price}</li> : null}
                </ul>
              </li>
            );
          }
        }
      });
    });
  };

  // Process each type of bet (H2H, spreads, totals)
  h2hOdds.forEach((game) => processBets(game, 'h2h', arbitrageBets, totalsBets));
  spreadOdds.forEach((game) => processBets(game, 'spreads', arbitrageBets, totalsBets));
  totalOdds.forEach((game) => processBets(game, 'totals', arbitrageBets, totalsBets));

  return (
    <div>
      <h1>NBA Arbitrage Odds</h1>

      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        {/* H2H Column */}
        <div style={{ flex: 1, padding: '0 10px' }}>
          <h2>Arbitrage H2H</h2>
          <ul>
            {arbitrageBets.length > 0 ? arbitrageBets : <li>No arbitrage opportunities found.</li>}
          </ul>
        </div>

        {/* Spreads Column */}
        <div style={{ flex: 1, padding: '0 10px' }}>
          <h2>Arbitrage Spreads</h2>
          <ul>
            {arbitrageBets.length > 0 ? arbitrageBets : <li>No arbitrage opportunities found.</li>}
          </ul>
        </div>

        {/* Totals Column */}
        <div style={{ flex: 1, padding: '0 10px' }}>
          <h2>Arbitrage Totals</h2>
          <ul>
            {arbitrageBets.length > 0 ? arbitrageBets : <li>No arbitrage opportunities found.</li>}
          </ul>
        </div>
      </div>

      {/* Odds Column */}
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        {/* H2H Column */}
        <div style={{ flex: 1, padding: '0 10px' }}>
          <h2>Odds H2H</h2>
          <ul>
            {totalsBets.length > 0 ? totalsBets : <li>No totals bets found.</li>}
          </ul>
        </div>

        {/* Spreads Column */}
        <div style={{ flex: 1, padding: '0 10px' }}>
          <h2>Odds Spreads</h2>
          <ul>
            {totalsBets.length > 0 ? totalsBets : <li>No totals bets found.</li>}
          </ul>
        </div>

        {/* Totals Column */}
        <div style={{ flex: 1, padding: '0 10px' }}>
          <h2>Odds Totals</h2>
          <ul>
            {totalsBets.length > 0 ? totalsBets : <li>No totals bets found.</li>}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default App;
