import React, { useEffect, useState } from 'react';
import { fetchGameIds, fetchPlayerPropsForGame } from '../api';

const PlayerProps = () => {
  const [playerProps, setPlayerProps] = useState<any[]>([]);

  useEffect(() => {
    const loadPlayerProps = async () => {
      try {
        const ids = await fetchGameIds();

        // Fetch all player props in parallel for each game
        const propPromises = ids.map((id) => fetchPlayerPropsForGame(id));
        const propsData = await Promise.all(propPromises);

        setPlayerProps(propsData);
        console.log('All player props:', propsData);
      } catch (error) {
        console.error('Failed to fetch player props:', error);
      }
    };

    loadPlayerProps();
  }, []);

  return (
    <div>
      <h2>Player Props</h2>
      {playerProps.length === 0 ? (
        <p>Loading...</p>
      ) : (
        playerProps.map((game, index) => (
          <div key={index} style={{ marginBottom: '1rem', padding: '1rem', border: '1px solid #ccc' }}>
            <h3>{game.home_team} vs {game.away_team}</h3>

            {/* Iterate over the bookmakers */}
            {game.bookmakers?.map((bookmaker: any) => (
              <div key={bookmaker.key} style={{ marginBottom: '1rem' }}>
                <h4>{bookmaker.title}</h4>

                {/* Iterate over player assists market */}
                {bookmaker.markets.map((market: any) => (
                  market.key === 'player_assists' && (
                    <div key={market.key}>
                      {/* Group the outcomes (player assists) by player */}
                      {market.outcomes.map((outcome: any) => (
                        <div key={outcome.description} style={{ marginBottom: '10px' }}>
                          <strong>{outcome.description}</strong> {/* Player Name */}
                          <ul>
                            <li>
                              {outcome.name}: {outcome.price} (Points: {outcome.point})
                            </li>
                          </ul>
                        </div>
                      ))}
                    </div>
                  )
                ))}
              </div>
            ))}
          </div>
        ))
      )}
    </div>
  );
};

export default PlayerProps;
